<?php

$pic = isset($_POST['pic']) ? $_POST['pic'] : '';

//$pic = "data:image/png;base64,";

$ok = true;
$messages = array();

// Загрузка штампа и фото, для которого применяется водяной знак (называется штамп или печать)
$stamp = imagecreatefrompng('https://raw.githubusercontent.com/mtytos/Hackaton-PhotoLab-TikTok/master/one.png');
$im = imagecreatefrompng($pic);

// Установка полей для штампа и получение высоты/ширины штампа
$marge_right = 10;
$marge_bottom = 10;
$sx = imagesx($stamp);
$sy = imagesy($stamp);

// Копирование изображения штампа на фотографию с помощью смещения края
// и ширины фотографии для расчета позиционирования штампа.
imagecopy($im, $stamp, imagesx($im) - $sx - $marge_right, imagesy($im) - $sy - $marge_bottom, 0, 0, imagesx($stamp), imagesy($stamp));

// Вывод и освобождение памяти
//header('Content-type: image/png');
$npath = "test.jpg";
imagejpeg($im, $npath);
imagedestroy($im);

$opp = "../tmp/" .$npath;

if ($pic) {
    $ok = true;
    $link = $opp;
    $messages[] = 'Получил посылку все ок. ' . $opp;
}
else {
    $ok = false;
    $link = $opp;
    $messages[] = 'Посылка где-то потерялась';
}

echo json_encode(
    array(
        'ok' => $ok,
        'link' => $link,
        'messages' => $messages
    )
);